namespace Phpno1\Test\CalculationTest;


use Phpno1\Algorithm\Calculation\SumString;
use PHPUnit\Framework\TestCase;

class SumStringTest extends TestCase
{
    private $sumStringObject;

    public function setUp()
    {
        parent::setUp();

        return $this->sumStringObject = new SumString();
    }

    public function testExamples() {
        $this->assertEquals('579', $this->sumStringObject->sumString('123', '456'));
        $this->assertEquals('', $this->sumStringObject->sumString('', ''));
        $this->assertEquals('', $this->sumStringObject->sumString('', ''));
    }
}